        animal_ptr->show_name();
        cout << "我的重量是 " << animal_ptr->get_weight()<<"(kg)" <<endl;
      }
      cout << "==================="<<endl;
      continue;
    }
    else 
    {
       cout << " wow, qq, We don't have your animal !!" <<endl;
       continue; 
    }

    cout << " How many days you want to feed ?? " << endl;
    cin >> food_value; 
    animal_ptr->feed(food_value);

  }while(1);

  return 0;
}

```

